        and BSC in

            '${bsc}'



) A) B
where B.rownumber >${startIndex}

 select top ${cnt} Date,BSC,MO,STATE,BLSTATE,BLO,BLA,LMO,BTS,CONF
from (select row_number() over(order by A.Date) as rownumber,*
      from (

    select Date,BSC,MO,STATE,BLSTATE,BLO,BLA,LMO,BTS,CONF from cdd_RXMSP_TG_${date} where 1=1

        and BSC in

            '${bsc}'



      ) A) B
where B.rownumber >${startIndex}

 select top ${cnt} Date,BSC,MO,CELL,CHGR
from (select row_number() over(order by A.Date) as rownumber,*
      from (

    select Date,BSC,MO,CELL,CHGR from cdd_RXTCP_${date} where 1=1

        and BSC in

            '${bsc}'



        and CELL in

            '${cell}'



      ) A) B
where B.rownumber >${startIndex}
